import React from 'react';
import './App.css'
import Navbar from './Navbar';
import Sidebar from './Sidebar';


// PRIORITIES
const PRIORITY_HIGHEST = "HIGHEST";
const PRIORITY_HIGH = "HIGH";
const PRIORITY_MEDIUM = "MEDIUM";
const PRIORITY_LOW = "LOW";
const PRIORITY_TRIVIAL = "TRIVIAL";
// STATUS
const TYPE_TODO = "TODO";
const TYPE_INPROGRESS = "INPROGRESS";
const TYPE_REVIEW = "Review";
const TYPE_DONE = "DONE";
// TITLES
const TITLE_TODO = "To do";
const TITLE_INPROGRESS = "In Progress";
const TITLE_REVIEW = "Review";
const TITLE_DONE = "Done";

// TASK
const CLASS_TASK = "taskCard";
const CLASS_COLUMN = "boardColumn";

// GENERAL JSON
const data = {
	tasks: [
		{
			id: "REACT-1",
			priority: PRIORITY_HIGHEST,
			title: "Read WebPack documentation",
			epicLink: "React buidings",
			status: TYPE_TODO,
			points: 3
		},
		{
			id: "REACT-2",
			priority: PRIORITY_HIGHEST,
			title: "Install dependencies",
			epicLink: "React buidings",
			status: TYPE_TODO,
			points: 3
		},
		{
			id: "REACT-3",
			priority: PRIORITY_HIGH,
			title: "Try Hello world",
			epicLink: "React buidings",
			status: TYPE_TODO
		},
		{
			id: "REACT-3",
			priority: PRIORITY_HIGH,
			title: "Try Hello world",
			epicLink: "React buidings",
			status: TYPE_TODO
		},
		{
			id: "REACT-3",
			priority: PRIORITY_HIGH,
			title: "Try Hello world",
			epicLink: "React buidings",
			status: TYPE_INPROGRESS
		},
		{
			id: "REACT-4",
			priority: PRIORITY_MEDIUM,
			title: "Create some little components",
			epicLink: "React buidings",
			status: TYPE_INPROGRESS
		},
		{
			id: "REACT-5",
			priority: PRIORITY_MEDIUM,
			title: "Create App with react-router",
			epicLink: "React buidings",
			status: TYPE_INPROGRESS
		},
		{
			id: "REACT-6",
			priority: PRIORITY_MEDIUM,
			title: "Practise styled-components (react library)",
			epicLink: "React buidings",
			status: TYPE_REVIEW
		},
		{
			id: "REACT-7",
			priority: PRIORITY_LOW,
			title: "Read Redux documentation",
			epicLink: "React buidings",
			status: TYPE_REVIEW
		},
		{
			id: "REACT-8",
			priority: PRIORITY_LOW,
			title: "Do a little project with ReduxJS",
			epicLink: "React buidings",
			status: TYPE_REVIEW
		},
		{
			id: "REACT-8",
			priority: PRIORITY_LOW,
			title: "Do a little project with ReduxJS",
			epicLink: "React buidings",
			status: TYPE_DONE
		},
	]
};

const TaskCard = (task) => {
	const image =
		"https://media.licdn.com/mpr/mpr/shrinknp_200_200/AAEAAQAAAAAAAAemAAAAJGFkOGZiYTIwLTU0YjUtNGY0Ni1hM2ZhLThlY2Y2NzU0Mzk3MA.jpg";
	const priorityclass = getPriorityClass(task.priority);

	function getPriorityClass(priority) {
		if (priority === PRIORITY_LOW) {
			return CLASS_TASK + "--" + PRIORITY_LOW;
		} else if (priority === PRIORITY_MEDIUM) {
			return CLASS_TASK + "--" + PRIORITY_MEDIUM;
		} else if (priority === PRIORITY_HIGH) {
			return CLASS_TASK + "--" + PRIORITY_HIGH;
		} else if (priority === PRIORITY_HIGHEST) {
			return CLASS_TASK + "--" + PRIORITY_HIGHEST;
		}
	}

	return (
		<div className={`taskCard ${priorityclass}`}>
			<header className="taskCard__header">
				<h4 className="taskCard__title">{task.title}</h4>
				<div className="taskCard__epicLink-wrapper">
					<span className="taskCard__epicLink-title">{task.epicLink}</span>
				</div>
			</header>

			<footer className="taskCard__footer">
				<span className="taskCard__type">{task.priority}</span>
				<div>
					<span className="taskCard__id">{task.id}</span>
					<img className="taskCard__avatar" src={image} />
				</div>
			</footer>
		</div>
	);
};

const BoardColumn = (props) => {
	const columnClass = getColumnClass(props.title);
	function getColumnClass(title) {
		if (title === TITLE_TODO) {
			return CLASS_COLUMN + "--" + TYPE_TODO;
		} else if (title === TITLE_INPROGRESS) {
			return CLASS_COLUMN + "--" + TYPE_INPROGRESS;
		} else if (title === TITLE_DONE) {
			return CLASS_COLUMN + "--" + TYPE_DONE;
		}
	}

	return (
		<div className={`boardColumn ${columnClass}`}>
			<header className="boardColumn__header">
				<h1 className="boardColumn__title">{props.title}</h1>
			</header>
			<div className="boardColumn__taskList">
				{props.tasks.map((task) => (
					<TaskCard {...task} />
				))}
			</div>
		</div>
	);
};

class Board extends React.Component {
	constructor() {
		super();
		this.state = {
			tasks: data.tasks
		};
	}

	render() {
		return (
			<div className="board">
				<BoardColumn
					title={TITLE_TODO}
					tasks={this.state.tasks.filter((task) => {
						return task.status === TYPE_TODO;
					})}
				/>

				<BoardColumn
					title={TITLE_INPROGRESS}
					tasks={this.state.tasks.filter((task) => {
						return task.status === TYPE_INPROGRESS;
					})}
				/>

				<BoardColumn
					title={TITLE_REVIEW}
					tasks={this.state.tasks.filter((task) => {
						return task.status === TYPE_REVIEW;
					})}
				/>
				<BoardColumn
					title={TITLE_DONE}
					tasks={this.state.tasks.filter((task) => {
						return task.status === TYPE_DONE;
					})}
				/>
			</div>
		);
	}
}


class App extends React.Component {
	render() {
		return (
			<div class="app">
			    <Navbar/>
				<Sidebar/>
				<Board />				
			</div>
		);
	}
}

export default App;
